import React from 'react';

class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      imageURL: '',
      responseImgae: null,
      image_original: null,
      image_response: null
    };
    // var responseImage 

    this.handleUploadImage = this.handleUploadImage.bind(this);
  }

  handleUploadImage(ev) {
    ev.preventDefault();
    const data = new FormData();
    this.setState({image_original:this.uploadInput.files[0]})
    let reader = new FileReader();

    reader.onloadend = () => {
      this.setState({image_original:reader.result})

    }
    reader.readAsDataURL(this.uploadInput.files[0])

    data.append('file', this.uploadInput.files[0]);
    data.append('filename', this.fileName.value);
    var response_image;

    fetch('http://localhost:8080/upload', {
          method: 'POST',
          body: data,
        })
        .then(response => response.blob())
        .then(
          img => {
            console.log(img)
            // this.setState({image:img})
            response_image = URL.createObjectURL(img)
            console.log(response_image)

            // var reader = new FileReader();
            // reader.readAsDataURL(img); 
            // reader.onloadend = function() {
            //     var base64data = reader.result;                
            //     // this.setState({image:base64data})
            //     return base64data
            // }
            this.setState({image_response:response_image})
          }
        )


  }

  render() {
    return (
      <form onSubmit={this.handleUploadImage}>
        <div>
          <input ref={(ref) => { this.uploadInput = ref; }} type="file" />
        </div>

        <br />
        <div>
          <button>Upload</button>
        </div>

        <img src={this.state.image_original} width="256" height="256"/>
        <img src={this.state.image_response} width="256" height="256"/>
      </form>
    );
  }
}

export default Main;
